@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

:root {
  /* Updated theme for gaming industry blog with blue accent and rich neutrals */
  --background: oklch(0.99 0 0);
  --foreground: oklch(0.18 0 0);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.18 0 0);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.18 0 0);
  --primary: oklch(0.47 0.18 252);
  --primary-foreground: oklch(0.99 0 0);
  --secondary: oklch(0.95 0 0);
  --secondary-foreground: oklch(0.18 0 0);
  --muted: oklch(0.96 0 0);
  --muted-foreground: oklch(0.5 0 0);
  --accent: oklch(0.96 0.02 252);
  --accent-foreground: oklch(0.18 0 0);
  --destructive: oklch(0.577 0.245 27.325);
  --destructive-foreground: oklch(0.99 0 0);
  --border: oklch(0.9 0 0);
  --input: oklch(0.9 0 0);
  --ring: oklch(0.47 0.18 252);
  --chart-1: oklch(0.646 0.222 41.116);
  --chart-2: oklch(0.6 0.118 184.704);
  --chart-3: oklch(0.398 0.07 227.392);
  --chart-4: oklch(0.828 0.189 84.429);
  --chart-5: oklch(0.769 0.188 70.08);
  --radius: 0.75rem;
  --sidebar: oklch(0.985 0 0);
  --sidebar-foreground: oklch(0.145 0 0);
  --sidebar-primary: oklch(0.205 0 0);
  --sidebar-primary-foreground: oklch(0.985 0 0);
  --sidebar-accent: oklch(0.97 0 0);
  --sidebar-accent-foreground: oklch(0.205 0 0);
  --sidebar-border: oklch(0.922 0 0);
  --sidebar-ring: oklch(0.708 0 0);
}

.dark {
  /* Dark mode theme for gaming blog */
  --background: oklch(0.14 0 0);
  --foreground: oklch(0.97 0 0);
  --card: oklch(0.18 0 0);
  --card-foreground: oklch(0.97 0 0);
  --popover: oklch(0.18 0 0);
  --popover-foreground: oklch(0.97 0 0);
  --primary: oklch(0.6 0.2 252);
  --primary-foreground: oklch(0.99 0 0);
  --secondary: oklch(0.22 0 0);
  --secondary-foreground: oklch(0.97 0 0);
  --muted: oklch(0.22 0 0);
  --muted-foreground: oklch(0.62 0 0);
  --accent: oklch(0.24 0.03 252);
  --accent-foreground: oklch(0.97 0 0);
  --destructive: oklch(0.396 0.141 25.723);
  --destructive-foreground: oklch(0.97 0 0);
  --border: oklch(0.26 0 0);
  --input: oklch(0.26 0 0);
  --ring: oklch(0.6 0.2 252);
  --chart-1: oklch(0.488 0.243 264.376);
  --chart-2: oklch(0.696 0.17 162.48);
  --chart-3: oklch(0.769 0.188 70.08);
  --chart-4: oklch(0.627 0.265 303.9);
  --chart-5: oklch(0.645 0.246 16.439);
  --sidebar: oklch(0.205 0 0);
  --sidebar-foreground: oklch(0.985 0 0);
  --sidebar-primary: oklch(0.488 0.243 264.376);
  --sidebar-primary-foreground: oklch(0.985 0 0);
  --sidebar-accent: oklch(0.269 0 0);
  --sidebar-accent-foreground: oklch(0.985 0 0);
  --sidebar-border: oklch(0.269 0 0);
  --sidebar-ring: oklch(0.439 0 0);
}

@theme inline {
  --font-sans: "Geist", "Geist Fallback";
  --font-mono: "Geist Mono", "Geist Mono Fallback";
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-destructive-foreground: var(--destructive-foreground);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
  }

  /* Custom blog post styling for markdown content */
  .blog-content h2 {
    @apply text-2xl font-bold mb-4 text-foreground;
  }

  .blog-content h3 {
    @apply text-xl font-semibold mb-3 text-foreground;
  }

  .blog-content ul {
    @apply space-y-2 mb-6;
  }

  .blog-content li {
    @apply text-foreground/90 leading-relaxed;
  }

  .blog-content blockquote {
    @apply border-l-4 border-primary pl-6 py-4 my-6 italic text-foreground/80 bg-accent/40 rounded-r-lg;
  }

  .blog-content hr {
    @apply my-8 border-border opacity-0;
  }

  .blog-content p {
    @apply text-foreground/90 leading-relaxed mb-4;
  }
}

interface BlogPostLayoutProps {
  title: string
  date: string
  contentHtml: string
}

export function BlogPostLayout({ title, date, contentHtml }: BlogPostLayoutProps) {
  // Split content by h2 headers to create individual cards
  const sections = contentHtml.split(/<h2/g).filter(Boolean)

  return (
    <article className="min-h-screen bg-background py-12 px-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <header className="mb-12">
          <time className="text-sm font-medium text-muted-foreground tracking-wide uppercase">{date}</time>
          <h1 className="mt-3 text-4xl md:text-5xl font-bold text-balance text-foreground leading-tight">{title}</h1>
        </header>

        {/* Content sections as cards */}
        <div className="space-y-6">
          {sections.map((section, index) => {
            // Re-add the h2 tag we split on
            const sectionContent = index === 0 && !contentHtml.startsWith("<h2") ? section : `<h2${section}`

            return (
              <div
                key={index}
                className="bg-card border border-border rounded-xl p-8 shadow-sm hover:shadow-md transition-shadow duration-200"
              >
                <div
                  className="blog-content prose prose-slate max-w-none"
                  dangerouslySetInnerHTML={{ __html: sectionContent }}
                />
              </div>
            )
          })}
        </div>
      </div>
    </article>
  )
}

