interface BlogPostLayoutProps {
  title: string
  date: string
  contentHtml: string
}

export function BlogPostLayout({ title, date, contentHtml }: BlogPostLayoutProps) {
  return (
    <article className="min-h-screen bg-background">
      {/* Header section with dark accent */}
      <header className="border-b-2 border-foreground/20 bg-gradient-to-b from-foreground/5 to-transparent">
        <div className="mx-auto max-w-4xl px-6 py-12 md:px-8 md:py-16">
          <time className="mb-4 inline-block rounded-full bg-foreground px-4 py-1.5 font-mono text-sm font-medium text-primary-foreground">
            {date}
          </time>
          <h1 className="text-balance font-sans text-4xl font-bold leading-tight tracking-tight text-foreground md:text-5xl lg:text-6xl">
            {title}
          </h1>
        </div>
      </header>

      {/* Main content with custom markdown styling */}
      <div className="mx-auto max-w-4xl px-6 py-12 md:px-8 md:py-16">
        <div className="prose-blog" dangerouslySetInnerHTML={{ __html: contentHtml }} />
      </div>
    </article>
  )
}

