interface BlogPostLayoutProps {
  title: string
  date: string
  contentHtml: string
}

export function BlogPostLayout({ title, date, contentHtml }: BlogPostLayoutProps) {
  return (
    <article className="min-h-screen bg-white">
      <div className="mx-auto max-w-3xl px-6 py-16 md:py-24">
        {/* Header */}
        <header className="mb-16 space-y-4">
          <time className="text-sm font-medium uppercase tracking-wider text-zinc-500">{date}</time>
          <h1 className="text-balance text-5xl font-bold leading-tight tracking-tight text-zinc-900 md:text-6xl">
            {title}
          </h1>
        </header>

        {/* Content */}
        <div className="prose-blog" dangerouslySetInnerHTML={{ __html: contentHtml }} />
      </div>
    </article>
  )
}

@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

