@theme inline {
  --font-sans: "Geist", "Geist Fallback";
  --font-mono: "Geist Mono", "Geist Mono Fallback";
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-destructive-foreground: var(--destructive-foreground);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
  }

  /* Added custom blog post styling for markdown content */
  .prose-blog {
    @apply text-base leading-relaxed text-zinc-700 md:text-lg md:leading-relaxed;
  }

  /* H2 - Game names (large, bold, lots of space) */
  .prose-blog h2 {
    @apply mb-6 mt-20 text-4xl font-bold leading-tight tracking-tight text-zinc-900 first:mt-0 md:text-5xl;
  }

  /* H3 - Topic subheaders */
  .prose-blog h3 {
    @apply mb-4 mt-12 text-2xl font-bold leading-snug text-zinc-900 md:text-3xl;
  }

  /* Paragraphs */
  .prose-blog p {
    @apply mb-6;
  }

  /* Bullet lists with custom markers */
  .prose-blog ul {
    @apply mb-8 space-y-3 pl-0;
  }

  .prose-blog ul li {
    @apply relative pl-8;
  }

  .prose-blog ul li::before {
    content: "";
    @apply absolute left-0 top-[0.6em] h-2 w-2 rounded-full bg-orange-500;
  }

  /* Nested lists */
  .prose-blog ul ul {
    @apply mb-4 mt-3;
  }

  /* Blockquotes with thick left border accent */
  .prose-blog blockquote {
    @apply my-8 border-l-4 border-orange-500 bg-zinc-50 py-6 pl-8 pr-6 italic text-zinc-800;
  }

  .prose-blog blockquote p {
    @apply mb-0;
  }

  /* Horizontal rules as section dividers */
  .prose-blog hr {
    @apply my-16 border-0 border-t border-zinc-200;
  }

  /* Strong text */
  .prose-blog strong {
    @apply font-bold text-zinc-900;
  }

  /* Links with subtle accent */
  .prose-blog a {
    @apply font-medium text-zinc-900 underline decoration-orange-500 decoration-2 underline-offset-2 transition-colors hover:text-orange-600;
  }

  /* Ordered lists */
  .prose-blog ol {
    @apply mb-8 space-y-3 pl-6;
  }

  .prose-blog ol li {
    @apply pl-2;
  }

  /* Code (inline) */
  .prose-blog code {
    @apply rounded bg-zinc-100 px-1.5 py-0.5 text-sm font-mono text-zinc-900;
  }

  /* Preformatted code blocks */
  .prose-blog pre {
    @apply mb-8 overflow-x-auto rounded-lg bg-zinc-900 p-6 text-sm text-zinc-100;
  }

  .prose-blog pre code {
    @apply bg-transparent p-0 text-zinc-100;
  }
}

